mapper.insert:
INSERT INTO product 
(
    product_name, product_description, price, 
    publisher_id, type
)
VALUES
(
    #{productName}, #{productDescription}, #{price},
    #{publisherId}, #{type}
)

mapper.select:
SELECT * FROM product


        AND type = #{type}



        AND price >= #{minPrice}



        AND price <= #{maxPrice}



        AND update_time > #{startTime}



ORDER BY update_time DESC
